package com.librum.utils

import android.content.Context
import android.webkit.URLUtil
import com.librum.ui.reader.EpubReaderActivity
import com.librum.utils.enums.EpubSourceType
import org.jetbrains.anko.startActivity

/**
 * @author lusinabrian on 06/12/17.
 * @Notes Reader object to open the epub book given the url of the book
 * Entry point to epub reader
 */
object LibrumReader {
    /**
     * Opens the given book/epub document given the url/asset/sd card path
     * @param context Context of the calling Activity
     * @param bookUrl Url of the book to open
     * */
    @JvmStatic
    fun openBook(context: Context, bookUrl: String) {
        // check if the bookUrl is a valid url to use, if empty, then defer to use raw resource id
        if (!bookUrl.isBlank()) {
            if (isAssetPath(bookUrl)) {
                // check the type of the url is an asset or sd card path
                context.startActivity<EpubReaderActivity>(INTENT_KEY_BOOK_ASSET to bookUrl,
                        INTENT_EPUB_SOURCE_TYPE to EpubSourceType.SOURCE_ASSET)
            } else if (isValidUrl(bookUrl)) {
                // check if url is a valid URI
                context.startActivity<EpubReaderActivity>(INTENT_KEY_BOOK_URL to bookUrl,
                        INTENT_EPUB_SOURCE_TYPE to EpubSourceType.SOURCE_URI)
            }
        }

        // throw error if above checks fail
        throw LibrumError("Invalid Book Url $bookUrl")
    }

    /**
     * Opens the given book/epub document given the url/asset/sd card path
     * @param context Context of the calling Activity
     * @param rawResourceId Raw resource file id
     * */
    @JvmStatic
    fun openBook(context: Context, rawResourceId: Int) {
        // check if raw resource id is not equal to 0,
        if (rawResourceId != 0) {
            // open as a resource
            context.startActivity<EpubReaderActivity>(INTENT_KEY_BOOK_RAW to rawResourceId,
                    INTENT_KEY_BOOK_RAW to EpubSourceType.SOURCE_RAW)
        }
        throw LibrumError("Invalid Raw Resource id passed $rawResourceId")
    }

    private fun isAssetPath(bookUrl: String) = URLUtil.isAssetUrl(bookUrl)

